void Battery::drawBatteryIcon(int x, int y, int batteryStage) {
  // Определение массива в зависимости от заряда АКБ
  const uint16_t (*batteryIcon)[21];

  if (batteryStage >= 90) {
    batteryIcon = BatteryStageFull;
  } else if (batteryStage > 75 && batteryStage <= 90) {
    batteryIcon = BatteryStage90;
  } else if (batteryStage > 50 && batteryStage <= 75 ) {
    batteryIcon = BatteryStage75;
  } else if (batteryStage > 25 && batteryStage <= 50) {
    batteryIcon = BatteryStage50;
  } else if (batteryStage > 10 && batteryStage <= 25 ) {
    batteryIcon = BatteryStage25;
  } else if (batteryStage > 0 && batteryStage <= 10) {
    batteryIcon = BatteryStageLow;
  }

  // Отрисовка пикселей
  for (int i = 0; i < 11; ++i) {
    for (int j = 0; j < 21; ++j) {
      uint16_t color = batteryIcon[i][j];
      dInstance.getDisplay().drawPixel(x + j, y + i, color);
    }
  }
}